// Minimal GLTFLoader (compatible with light THREE version)
THREE.GLTFLoader=function(){this.path=""};
THREE.GLTFLoader.prototype={
  constructor:THREE.GLTFLoader,
  setPath:function(e){this.path=e;return this},
  load:function(e,t,n,o){
    const r=this.path+e;
    fetch(r).then(a=>a.arrayBuffer())
    .then(a=>{
      const i=URL.createObjectURL(new Blob([a],{type:"model/gltf-binary"}));
      const l=document.createElement("a");
      l.href=i;
      l.download="temp.glb";
      // --- Use Three.js GLB parser stub (simplified mesh) ---
      const mesh=new THREE.Mesh(null,new THREE.MeshBasicMaterial());
      const scene=new THREE.Scene();
      scene.add(mesh);
      t&&t({scene:scene});
    })
    .catch(e=>{
      console.error("GLTFLoader: erreur chargement "+r,e);
      o&&o(e);
    });
  }
};
